////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/// Developed for Pearson by Robosoft Technologies Pvt. Ltd.
/// (c) Pearson Inc.
///
///Abdul Asif
///
////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

import com.eviware.soapui.impl.wsdl.teststeps.*
import com.eviware.soapui.support.types.StringToStringsMap



def headers = new StringToStringsMap()

def Authorization = testRunner.testCase.testSuite.project.getPropertyValue("SuperAuthorization")
log.info Authorization

headers.put("Accept","application/json, text/plain, */*")
headers.put("Authorization", Authorization)

testRunner.testCase.getTestStepByName("ProficiencyScaleSettings-Positive").getTestRequest().setRequestHeaders(headers)


// get a reference to the library APILibrarySet 
library = testRunner.testCase.testSuite.project.testSuites["Utilities"]  
module = library.testCases["libraries"].testSteps["Env"]  
module.run(testRunner, context)  
def env  = context.env   
env.setEndpoint(testRunner.testCase.testSuite.project.getPropertyValue("URL"))

def testRequest = testRunner.testCase.getTestStepByName("ProficiencyScaleSettings-Positive").testRequest

httpRequest =testRequest
propertyHolder = httpRequest.getParams()   

   if(propertyHolder){
      queryString = changeOid(httpRequest,propertyHolder)
   }else{
      queryString = ""
   }

def changeOid(httpRequest,propertyHolder){    
   queryString = "?"   
   for( properties in propertyHolder){
   	if (properties.key == "orgId")
   	{
      log.info "Name is ${properties.key}"
      value1 = properties.key
      value = httpRequest.getProperty("${value1}").getValue()
       log.info "Value is ${value}"
        httpRequest.getProperty("${value1}").setValue(testRunner.testCase.testSuite.project.getPropertyValue("ByOid"))
      if(value){
         if(queryString.length() == 1){
            queryString = queryString + properties.key + "=" + value
         }else{
            queryString = queryString + "&" + properties.key + "=" + value
         }
      }      
   }   
   }
   log.info "queryString is ${value}"   
   return queryString
}

con:configuration.scriptText:
import groovy.json.JsonSlurper
// RESPONSE Asserts def response = messageExchange.response.responseContent def resHeaders = messageExchange.responseHeaders log.warn response
assert resHeaders["#status#"][0].contains("200 OK") try { assert response != null assert ! response.contains("Service Temporarily Unavailable") assert ! response.contains("not authorized") assert ! response.contains("User does not exist.")
//JSON Asserts def slurper = new JsonSlurper() def json = slurper.parseText response
assert json != null log.info json.proficiencyScaleSettings.size() assert json.proficiencyScaleSettings.size() >1 assert json.proficiencyScaleSettings.proficiencyScaleId != null assert json.proficiencyScaleSettings.prodOrgId != null assert json.proficiencyScaleSettings.proficiencyScaleName != null assert json.proficiencyScaleSettings.proficiencyScaleStartPctRange != null assert json.proficiencyScaleSettings.proficiencyScaleEndPctRange != null
assert json.prodOrgOptions.size() == 2 assert json.prodOrgOptionName[0] == "Require student demographic data:" || json.prodOrgOptionName[1] == "Require student demographic data:" assert json.prodOrgOptionName[1] == "Capture research data:" || json.prodOrgOptionName[0] == "Capture research data:" assert json.prodOrgOptionId != null assert json.prodOrgId != null assert json.prodOrgOptionValue != null } catch(Exception e) { log.warn "failed" }

