        httpRequest.getProperty("${value1}").setValue(testRunner.testCase.testSuite.project.getPropertyValue("ByOid"))
      if(value){
         if(queryString.length() == 1){
            queryString = queryString + properties.key + "=" + value
         }else{
            queryString = queryString + "&" + properties.key + "=" + value
         }
      }      
   }   
   }
