def response = messageExchange.response.responseContent def resHeaders = messageExchange.responseHeaders log.warn response
//RESPONSE asserts assert response == null assert resHeaders != null assert resHeaders["#status#"][0].contains("201 Created")
try { assert ! response.contains("Service Temporarily Unavailable") assert ! response.contains("ERROR: Organization Name Exists") assert ! response.contains("ERROR: Organization ID Exists") assert ! response.contains("Parent Organization Does Not Exist")
//JSON Asserts def slurper = new JsonSlurper() def json = slurper.parseText response
assert json == null assert json.status != "1" } catch(Exception e) { log.warn("failure") }

