import com.pst.testautomation.fitnesse.license.*
//com.eviware.soapui.support.GroovyUtils.registerJdbcDriver("com.pst.testautomation.fitnesse.license.GenerateLicenseBundleForOrg")
class GenerarteLicense { def log def context def testRunner def currResSet def ConcurrentUsersForTheBundleTo def ExpireOn def license
def GenerarteLicense(logIn,contextIn,testRunnerIn) { this.log = logIn this.context = contextIn this.testRunner = testRunnerIn }
def generateLicense() { GenerateLicenseBundleForOrg generateLicenseBundleForOrg = new GenerateLicenseBundleForOrg() try { generateLicenseBundleForOrg.setConcurrentUsersForTheBundleTo( 125 ) generateLicenseBundleForOrg.bundleWillExpireOn( "03/03/2016" ) generateLicenseBundleForOrg.addALicenseUsingTheLicenseModelForConcurrentUsers( "MATH", "SUBSCRIPTION", 110 ) license=generateLicenseBundleForOrg.generateLicense() log.info license return license } catch ( Exception Ex ) { Ex.printStackTrace() }
} }
